import requests
from requests.auth import HTTPBasicAuth
import base64
from datetime import datetime
import json
import os
import streamlit as st

class MpesaClient:
    def __init__(self, environment='sandbox'):
        self.consumer_key = os.getenv('MPESA_CONSUMER_KEY', 'your_consumer_key')
        self.consumer_secret = os.getenv('MPESA_CONSUMER_SECRET', 'your_consumer_secret')
        self.environment = environment

        if environment == 'sandbox':
            self.base_url = 'https://sandbox.safaricom.co.ke'
            self.business_short_code = "174379"
            self.passkey = "bfb279f9aa9bdbcf158e97dd71a467cd2e0c893059b10f78e6b72ada1ed2c919"
        else:
            self.base_url = 'https://api.safaricom.co.ke'
            self.business_short_code = os.getenv('MPESA_SHORTCODE', '174379')
            self.passkey = os.getenv('MPESA_PASSKEY', 'your_passkey')

    def get_access_token(self):
        """Generate access token for API authentication"""
        url = f"{self.base_url}/oauth/v1/generate?grant_type=client_credentials"

        try:
            response = requests.get(
                url, 
                auth=HTTPBasicAuth(self.consumer_key, self.consumer_secret),
                timeout=30
            )

            if response.status_code == 200:
                return response.json().get('access_token')
            else:
                st.error(f"Failed to get access token: {response.text}")
                return None

        except requests.exceptions.RequestException as e:
            st.error(f"Network error getting access token: {str(e)}")
            return None

    def stk_push(self, phone_number, amount, account_reference, transaction_desc, callback_url):
        """Initiate STK Push request"""
        access_token = self.get_access_token()

        if not access_token:
            return {"success": False, "error": "Failed to get access token"}

        # Clean phone number (remove spaces, dashes, etc.)
        phone_number = phone_number.replace(" ", "").replace("-", "").replace("+", "")

        # Ensure phone number starts with 254
        if phone_number.startswith("0"):
            phone_number = "254" + phone_number[1:]
        elif not phone_number.startswith("254"):
            phone_number = "254" + phone_number

        # STK Push URL
        url = f"{self.base_url}/mpesa/stkpush/v1/processrequest"

        # Headers
        headers = {
            'Authorization': f'Bearer {access_token}',
            'Content-Type': 'application/json'
        }

        # Generate password
        timestamp = datetime.now().strftime('%Y%m%d%H%M%S')
        password_string = self.business_short_code + self.passkey + timestamp
        password = base64.b64encode(password_string.encode()).decode('utf-8')

        # Request payload
        payload = {
            "BusinessShortCode": self.business_short_code,
            "Password": password,
            "Timestamp": timestamp,
            "TransactionType": "CustomerPayBillOnline",
            "Amount": int(amount),
            "PartyA": phone_number,
            "PartyB": self.business_short_code,
            "PhoneNumber": phone_number,
            "CallBackURL": callback_url,
            "AccountReference": account_reference,
            "TransactionDesc": transaction_desc
        }

        try:
            response = requests.post(url, json=payload, headers=headers, timeout=30)
            result = response.json()

            if response.status_code == 200 and result.get('ResponseCode') == '0':
                return {
                    "success": True,
                    "checkout_request_id": result.get('CheckoutRequestID'),
                    "merchant_request_id": result.get('MerchantRequestID'),
                    "customer_message": result.get('CustomerMessage')
                }
            else:
                return {
                    "success": False,
                    "error": result.get('errorMessage', 'STK Push failed'),
                    "error_code": result.get('errorCode')
                }

        except requests.exceptions.RequestException as e:
            return {"success": False, "error": f"Network error: {str(e)}"}

    def query_stk_status(self, checkout_request_id):
        """Query STK Push transaction status"""
        access_token = self.get_access_token()

        if not access_token:
            return {"success": False, "error": "Failed to get access token"}

        url = f"{self.base_url}/mpesa/stkpushquery/v1/query"

        headers = {
            'Authorization': f'Bearer {access_token}',
            'Content-Type': 'application/json'
        }

        timestamp = datetime.now().strftime('%Y%m%d%H%M%S')
        password_string = self.business_short_code + self.passkey + timestamp
        password = base64.b64encode(password_string.encode()).decode('utf-8')

        payload = {
            "BusinessShortCode": self.business_short_code,
            "Password": password,
            "Timestamp": timestamp,
            "CheckoutRequestID": checkout_request_id
        }

        try:
            response = requests.post(url, json=payload, headers=headers, timeout=30)
            result = response.json()

            return {
                "success": True,
                "result_code": result.get('ResultCode'),
                "result_desc": result.get('ResultDesc'),
                "data": result
            }

        except requests.exceptions.RequestException as e:
            return {"success": False, "error": f"Network error: {str(e)}"}

def initiate_deposit(phone_number, amount_ksh, user_id, description="Stock Tracker Hub Deposit"):
    """Initiate M-Pesa deposit"""
    client = MpesaClient()

    # For demo purposes, use a placeholder callback URL
    callback_url = "https://your-app.com/mpesa/callback"
    account_reference = f"USER{user_id}"

    result = client.stk_push(
        phone_number=phone_number,
        amount=amount_ksh,
        account_reference=account_reference,
        transaction_desc=description,
        callback_url=callback_url
    )

    return result

def process_deposit_callback(callback_data):
    """Process M-Pesa callback (would be called by M-Pesa servers)"""
    # This would typically be handled by a webhook endpoint
    # For demo purposes, we'll simulate successful processing

    result_code = callback_data.get('Body', {}).get('stkCallback', {}).get('ResultCode')

    if result_code == 0:  # Success
        # Extract payment details
        callback_metadata = callback_data.get('Body', {}).get('stkCallback', {}).get('CallbackMetadata', {}).get('Item', [])

        payment_details = {}
        for item in callback_metadata:
            if item.get('Name') == 'Amount':
                payment_details['amount'] = item.get('Value')
            elif item.get('Name') == 'MpesaReceiptNumber':
                payment_details['receipt'] = item.get('Value')
            elif item.get('Name') == 'PhoneNumber':
                payment_details['phone'] = item.get('Value')

        return {"success": True, "details": payment_details}

    return {"success": False, "error": "Payment failed"}
